@inproceedings{WagnerPresterRocheEtAl2016,
  author     = {Wagner, Gerit and Prester, Julian and Roche, Maria Patricia and Benlian, Alexander and Schryen, Guido},
  booktitle  = {International Conference on Information Systems},
  title      = {Factors Affecting the Scientific Impact of Literature Reviews - A Scientometric Study},
  year       = {2016},
  pages      = {1--24},
  url        = {http://aisel.aisnet.org/icis2016/GeneralIS/Presentations/9},
  abstract   = {Standalone literature reviews are fundamental in every scientific discipline. Their value is reflected by a profound scientific impact in terms of citations. Although previous empirical research has shown that this impact has a large variance, it is largely unknown which specific factors influence the impact of literature reviews. Against this background, the purpose of our study is to shed light on the driving factors that make a difference in the scientific impact of literature reviews. Our analysis of an exhaustive set of 214 IS literature reviews reveals that factors on the author level (e.g., expertise, collaboration, and conceptual feedback) and on the article level (e.g., methodological rigor) are significant and robust predictors of scientific impact over and above journal level factors. These insights enhance our understanding of what distinguishes highly cited literature reviews. In so doing, our study informs future guidelines on literature reviews and provides insights for prospective authors.}
}

@inproceedings{WagnerEmplSchryen2020,
  author     = {Wagner, Gerit and Empl, Philip and Schryen, Guido},
  booktitle  = {European Conference on Information Systems},
  title      = {Designing a Novel Strategy for Exploring Literature Corpora},
  year       = {2020},
  pages      = {1--17},
  url        = {https://aisel.aisnet.org/ecis2020_rp/44},
  abstract   = {Understanding a new literature corpus can be a grueling experience for junior scholars. Nevertheless, corresponding guidelines have not been updated for decades. We contend that the traditional strategy of skimming all papers and reading selected papers afterwards needs to be revised. Therefore, we design a new strategy that guides the overall exploratory process by prioritizing influential papers for initial reading, followed by skimming the remaining papers. Consistent with schemata theory, starting with indepth reading allows readers to acquire more substantial prior content schemata, which are representative for the literature corpus and useful in the following skimming process. To this end, we develop a prototype that identifies the influential papers from a set of PDFs, which is illustrated in a case study in the IT business value domain. With the new strategy, we envision a more efficient process of exploring unknown literature corpora.}
}

@inproceedings{WagnerPrester2021,
  author     = {Wagner, Gerit and Prester, Julian},
  booktitle  = {European Conference on Information Systems},
  title      = {A Framework of Platform Design Mechanisms, Platform Congruence and Value Creation on C2C Platforms},
  year       = {2021},
  pages      = {1--19},
  url        = {https://aisel.aisnet.org/ecis2021_rp/49},
  abstract   = {Digital platforms for knowledge work, such as Upwork, Freelancer.com and Amazon Mechanical Turk connect clients with millions of workers for a range of tasks, including software development, virtual assistance, and micro-tasks. Information systems research on this emergent phenomenon has gained traction in recent years regarding publication volume and research diversity. To identify relevant papers, to distinguish them from related types of digital platforms, and to guide future research, we conducted a scoping review, focusing on the information systems literature. Results are structured according to a theoretical framework of the knowledge work process, covering three phases: Worker-client matching, committing for future action, and executing commitments. While the first phase has been analyzed extensively, we contend that the main phases of the knowledge work process have received scant attention. In this emergent stage of extant research, our review identifies promising research directions to guide prospective studies.}
}

@inproceedings{PresterWagner2021,
  author     = {Prester, Julian and Wagner, Gerit},
  booktitle  = {International Conference on Information Systems},
  title      = {Contracting Decisions on Digital Markets for Knowledge Work Services: A Qualitative Systematic Review},
  year       = {2021},
  pages      = {1--17},
  url        = {https://aisel.aisnet.org/icis2021/sharing_econ/sharing_econ/6},
  abstract   = {A growing amount of research is dedicated to digital markets for knowledge work services and contracting decisions as one of their most critical bottlenecks. Large-scale studies with average samples exceeding 144,000 observations have considered over 70 variables explaining contracting decisions. Our review aims to facilitate the convergence towards commonly agreed categories of variables and overarching models of contracting decisions in this context. To mitigate the risk of propagating deflated p-values associated with large sample studies, we propose a robust version of vote-counting techniques. The principal findings of our research suggest that only a few variables related to the client-worker relationship, the bid and individual worker characteristics have a practically significant effect on worker selection, and that auction success is primarily affected by project value and client experience. These findings lay the groundwork for future research that will lead to better explanations of contracting decisions.}
}

@inproceedings{WagnerPresterSchryen2017,
  author     = {Wagner, Gerit and Prester, Julian and Schryen, Guido},
  booktitle  = {International Conference on Information Systems},
  title      = {Exploring the Scientific Impact of Information Systems Design Science Research - A Scientometric Study},
  year       = {2017},
  pages      = {1--24},
  url        = {http://aisel.aisnet.org/icis2017/ResearchMethods/Presentations/8},
  abstract   = {Design science is a fundamental research stream that contends its position in the information systems discipline. While ongoing debates address the relative importance of design science contributions in the information systems community, insights into the scientific impact of design science research (DSR) are missing and this lack of understanding arguably poses challenges to an informed discourse. To identify the most influential papers and those factors that explain their scientific impact, this paper presents an exploratory study of the scientific impact of DSR papers published in the AIS Senior Scholars' Basket of Journals. We uncover the current DSR landscape by taking stock of influential papers and theories and develop a model to explain the scientific impact of DSR papers. Our findings show that scientific impact is significantly explained by theorization and novelty. We discuss how the implications of our work can be projected on the overarching discourse on DSR.}
}

@inproceedings{SchusterWagnerSchryen2018,
  author     = {Schuster, Richard and Wagner, Gerit and Schryen, Guido},
  booktitle  = {International Conference on Information Systems},
  title      = {Information Systems Design Science Research and Cumulative Knowledge Development - An Exploratory Study},
  year       = {2018},
  pages      = {1--17},
  url        = {https://aisel.aisnet.org/icis2018/design/Presentations/3},
  abstract   = {Contributing to cumulative knowledge development is a central goal in Information Systems design science research (IS-DSR). IS design theories (ISDTs) have been recognized as a particularly important building block for the accumulation of design- oriented knowledge. Yet, there are limited insights into how follow-up research builds on IS design theories in terms of testing and extending. To address these issues, we present results from the first empirical analysis of how ISDTs have been tested and extended within and beyond IS journals. Our qualitative analysis of papers citing ISDTs uncovers an alarming paucity of follow-up research that builds on these ISDTs. Specifically, the overall number of papers testing and extending any of the selected ISDTs ranges in the single digits. To propose an actionable path forward, we formulate four specific guidelines on how the IS(-DSR) community can facilitate the cumulative extension of the IS knowledge base.}
}

