@inproceedings{PresterWagnerSchryen2018,
  author     = {Prester, Julian and Wagner, Gerit and Schryen, Guido},
  booktitle  = {International Conference on Information Systems},
  title      = {Classifying the Ideational Impact of IS Review Articles - A Natural Language Processing Based Approach},
  year       = {2018},
  pages      = {1--17},
  url        = {https://aisel.aisnet.org/icis2018/research/Presentations/1},
  abstract   = {By providing knowledge contributions and stimulating future research, review articles (RAs) play a vital role for cumulative knowledge development. Although many papers cite RAs, it is rarely transparent to which degree citation impact represents perfunctory citations as opposed to a deeper engagement with a RA‚Äôs knowledge contributions. This distinction between perfunctory and ideational impact has largely been neglected in the literature arguably because of the manual effort required for qualitative analysis. Against this background, our study aims at developing automated classifiers of ideational impact of IS RAs. We propose a machine learning model based on natural language processing to evaluate the feasibility of automated analyses. The evaluation results provide evidence for an effective and scalable classification approach that presents a reliable and reproducible solution to the ideational impact classification problem. We discuss implications for improving the capabilities of understanding how IS scholars build on their field‚Äôs body of knowledge.}
}

