  author     = {Weish√§upl, Eva and Kunz, Michael and Yasasin, Emrah and Wagner, Gerit and Prester, Julian and Schryen, Guido and Pernul, G√ºnther},
  booktitle  = {2nd International Workshop on Security in Highly Connected IT Systems},
  title      = {Towards an Economic Approach to Identity and Access Management Systems Using Decision Theory},
  year       = {2015},
  pages      = {1--5},
  abstract   = {Nowadays, providing employees with failure-free access to various systems, applications and services is a crucial factor for organizations‚Äô success as disturbances potentially inhibit smooth workflows and thereby harm productivity. However, it is a challenging task to assign access rights to employees‚Äô accounts within a satisfying time frame. In addition, the management of multiple accounts and identities can be very onerous and time consuming for the responsible administrator and therefore expensive for the organization. In order to meet these challenges, firms decide to invest in introducing an Identity and Access Management System (IAMS) that supports the organization by using policies to assign permissions to accounts, groups, and roles. In practice, since various versions of IAMSs exist, it is a challenging task to decide upon introduction of an IAMS. The following study proposes a first attempt of a decision support model for practitioners which considers four alternatives: Introduction of an IAMS with Role-based Access Control (RBAC) or without and no introduction of IAMS again with or without RBAC. To underpin the practical applicability of the proposed model, we parametrize and operationalize it based on a real world use case using input from an expert interview.}
}

@inproceedings{PareRaymondCastonguayEtAl2023,
  author     = {Par√©, Guy and Raymond, Louis and Castonguay, Alexandre and Wagner, Gerit},
  booktitle  = {Hawaii International Conference on System Sciences},
  title      = {Prospective Physicians' Intention to Adopt Artificial Intelligence: A Configurational Perspective},
  year       = {2023},
  pages      = {1--10},
  url        = {https://hdl.handle.net/10125/103000},
  abstract   = {Artificial intelligence (AI) drives transformation across medical specialities, requiring current and future generations of physicians to navigate ever-changing digital environments. In this context, prospective physicians will play a key role in adopting and applying AI-based health technologies, underlining the importance of understanding their knowledge, attitudes, and intentions toward AI. To dissociate corresponding profiles, we adopted a configurational perspective and conducted a two-stage survey study of 184 (t_0) and 138 (t_1) medical students at a Canadian medical school. Our principal findings corroborate the existence of distinct clusters in respondents‚Äô AI profiles. We refer to these profiles as the AI unfamiliar, the AI educated, and the AI positive, showing that each profile is associated with different intentions towards future AI use. These exploratory insights on the variety of AI profiles in prospective physicians underline the need for targeted and adaptive measures of education and outreach.}
}

@inproceedings{SchryenWagnerBenlian2015,
  author     = {Schryen, Guido and Wagner, Gerit and Benlian, Alexander},
  booktitle  = {International Conference on Information Systems},
  title      = {Theory of Knowledge for Literature Reviews - An Epistemological Model, Taxonomy and Empirical Analysis of IS Literature},
  year       = {2015},
  pages      = {1--22},
  url        = {http://aisel.aisnet.org/icis2015/proceedings/ResearchMethods/8},
  abstract   = {Literature reviews play an important role in the development of knowledge. Yet, we observe a lack of theoretical underpinning of and epistemological insights into how literature reviews can contribute to knowledge creation and have actually contributed in the IS discipline. To address these theoretical and empirical research gaps, we suggest a novel epistemological model of literature reviews. This model allows us to align different contributions of literature reviews with their underlying knowledge conversions - thereby building a bridge between the previously largely unconnected fields of literature reviews and epistemology. We evaluate the appropriateness of the model by conducting an empirical analysis of 173 IS literature reviews which were published in 39 pertinent IS journals between 2000 and 2014. Based on this analysis, we derive an epistemological taxonomy of IS literature reviews, which complements previously suggested typologies.}
}

@inproceedings{OuimetPareWagner2020,
  author     = {Ouimet, A. Grenier and Par√©, G. and Wagner, G.},
  booktitle  = {Administrative Sciences Association of Canada Conference},
  title      = {Why Do People Keep Consulting Physicians Online? A Study Of Teleconsultation Continuance Intention},
  year       = {2020},
  pages      = {1--32},
  abstract   = {Accessibility, continuity, and cost of care services present persistent challenges in today‚Äôs healthcare systems. Teleconsultation is considered an opportunity to address these challenges while maintaining quality of care. In this paper, we develop and empirically test an extended Expectation Confirmation Model via an online survey of Canadian users. Our findings reveal that the main determinants of continuance intention are user satisfaction, perceived usefulness, service quality, confirmation of expectations, and perceived ease of use. As user satisfaction, the main predictor, largely depends on initial expectations confirmation, managers should provide their employees with proper information about the possibilities, limitations and potential benefits of teleconsultation platforms and services. This study underscores the importance of taking context into account at the theorizing stage.}
}

@inproceedings{HassanPresterWagner2020,
  author     = {Hassan, Nik Rushdi and Prester, Julian and Wagner, Gerit},
  booktitle  = {European Conference on Information Systems},
  title      = {Seeking Out Clear And Unique Information Systems Concepts: A Natural Language Processing Approach},
  year       = {2020},
  pages      = {1--15},
  url        = {https://aisel.aisnet.org/ecis2020_rp/128},
  abstract   = {The goal of this study is to advance conceptual development and the growth of knowledge in the information systems (IS) field by placing the spotlight on a component of theory that is rarely discussed ‚Äì the native IS concept. Beginning with the assertion that concepts are not the same as constructs, we build the argument that concepts, which are observable sets of ideas, should take priority over constructs which are unobservable fictions and hypothetical entities. Using natural language processing (NLP) based principles and techniques, we extract a sample of the most important concepts in the IS field from a corpus of 245 highly cited IS review articles and 1,293 citing articles from the Senior Scholars‚Äô Basket of Journals to illustrate the extent to which the field agrees on their usage, their clarity and distinctiveness and how the field can move forward in enhancing its conceptual formation.}
}

@article{PareWagnerTateEtA2025,
  doi        = {10.1080/0960085X.2025.2550403},
  author     = {Par√©, Guy and Wagner, Gerit and Tate, Mary and Schryen, Guido and Templier, Mathieu},
  journal    = {European Journal of Information Systems},
  title      = {Theorising forward: positioning deductive elaboration in the Information Systems research repertoire},
  year       = {2025},
  url        = {https://www.tandfonline.com/doi/full/10.1080/0960085X.2025.2550403},
  abstract   = {Theorising plays a foundational role in Information Systems (IS) research. While the field has made important advances through theory borrowing, via adaptation and instantiation, as well as through contextualisation of established frameworks and models, comparatively little attention has been devoted to the elaboration of existing theories through structured, logic-driven approaches. This commentary problematises that imbalance and advances the concept of deductive theory elaboration as a valuable, yet underutilised, form of theorising in behavioural IS research. We define deductive theory elaboration as a process that extends existing theories by introducing conceptual modifications to their constructs, relationships, or boundary conditions prior to empirical testing. We distinguish this approach from related forms of theorising and propose a four-step framework supported by a repertoire of elaboration patterns for both variance and process theories. We also offer practical reporting guidelines to promote transparency and rigour in elaboration-based contributions. Our aim is to encourage more systematic elaboration efforts to enhance the precision, generalisability, and cumulative potential of IS theories, an optimistic vision of how behavioural IS research can evolve to meet the conceptual challenges of a rapidly transforming digital landscape.}
}

@phdthesis{Wagner2019,
  author     = {Wagner, Gerit},
  title      = {Explaining and Distinguishing Scientific Impact in Information Systems Research},
  year       = {2019},
  pages      = {1--217},
  abstract   = {Since its inception, the Information Systems discipline has been striving to develop impactful papers that contribute to cumulative knowledge development. Yet, there is a surprising lack of insights on how scientific impact can be accomplished and to which extent this impact represents a substantial engagement with, and extension of the knowledge contributions of the original papers. Especially for review articles and design science research, there are both competing conceptions of what makes these papers impactful and a lack of empirical evidence that would inform this debate. Furthermore, there is a latent skepticism as to whether this sometimes staggering impact of review articles actually represents knowledge development. In a similar way, it is unclear how and to which extent design science research has stimulated meaningful, cumulative knowledge development in information systems. The goal of this thesis is therefore to (1) explain and to (2) distinguish the scientific impact of review articles and design science research. Specifically, the first goal considers overall scientific impact as the dependent variable whose association with antecedent factors is analyzed by regression methodologies. The second goal zooms in on the concept of scientific impact and considers it as a relation between citing and cited papers that is explored through methodologies of manual content analysis and machine- learning classification.&lt;br&gt;With Paper 1, I develop the foundation of knowledge development through review articles by crystallizing their contributions and aligning them with their underlying knowledge conver- sion processes in an overarching framework. This framework is based on the abstraction and codification of knowledge and thereby integrates two essential dimensions of knowledge devel- opment. Overall, the foundation developed in the first paper informs the underlying conception of knowledge development of both review articles and citing papers.&lt;br&gt;Addressing the first goal, Papers 2 and 3 develop and test scientometric impact models ex- plaining the scientific impact of review articles and design science research, respectively. Beyond common control variables related to the journal and author level, they offer distinct insights for each type of paper. For review articles, I identify strong effects related to methodological transparency and the development of a research agenda, which vary depending on the type of review. For design science research, I show that theorization and novelty drive scientific impact.&lt;br&gt;Concerning the second goal, Papers 4 and 5 distinguish different types of scientific impact of review articles and design science research, respectively. To analyze the different types of impact that review articles have on their overwhelming number of citing papers, I develop machine learning classifiers. Specifically, I distinguish ideational impact, which corresponds to a substantial engagement with and development of the knowledge contributions of the review article, from perfunctory impact, which corresponds to more trivial connections to the review article. In a similar, though not automated way, I analyze the types of impact of information systems design theories, a particular type of design science research. These analyses primarily focus on whether follow-up research tests and extends these theories. Based on our content analysis, I identify an alarming paucity of follow-up research in this area and develop specific guidelines for the design science community to address this challenge.&lt;br&gt;The thesis concludes with an overview of the research contributions, implications for research practice, future research opportunities, and final remarks.},
  school     = {Universit√§t Regensburg}
}

